export const service = {
    auth,
    // authoriz,
    gettickets,
    // createquestion,
    createtask,
    addemployee,
}

async function auth(data){
    let responce = await fetch('http://test.4dev.kz:8890/token',
        {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Basic ' + btoa(data.email + ':' + data.password)
            }
        })
      return responce;
}

async function gettickets(data){
    let token = localStorage.getItem('access_token')
    let responce = await fetch('http://test.4dev.kz:8890/getList',
        {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + token
            },
            body: JSON.stringify(data)
        })
      return responce;
}

async function createTask(data){
    let responce = await fetch('http://test.4dev.kz:8890/',
        {
            method: 'post',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Bearer ' + localStorage.getItem('token')
            },
            body: JSON.stringify(data)
        })
      return responce;
}

async function addemployee(data){
    let responce = await fetch('http://192.168.31.36:8000/User/Create',
    {
        method: 'post',
        headers: {
            'Content-Type': 'application/json',
            'Authorization': 'Bearer ' + localStorage.getItem('token')
        },
        body: JSON.stringify(data)
    })
    return responce;
}
